import React from 'react';

function FlavorOption({ flavorOption }) {

function FlavorOption({ flavorOption, quantityOption, onFlavorChange, onQuantityChange }) {
    const handleAddQuantity = () => {
      onQuantityChange({ target: { value: quantityOption + 1 }});
    };

    const handleSubtractQuantity = () => {
      onQuantityChange({ target: { value: quantityOption - 1 }});
    };

    return (
      <div className="flavor-option">
        <button onClick={handleSubtractQuantity}>-</button>
        <select className="quantity-dropdown" value={quantityOption} onChange={onQuantityChange}>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          {/* Add more quantity options as needed */}
        </select>
        <button onClick={handleAddQuantity}>+</button>

        <select className="flavor-dropdown" value={flavorOption} onChange={onFlavorChange}>
          <option value="chocolate">Chocolate</option>
          <option value="vanilla">Vanilla</option>
          <option value="strawberry">Strawberry</option>
          {/* Add more flavor options as needed */}
        </select>
      </div>
    );
  }
}

export default FlavorOption;
