import React, {useState, Component} from "react";
import { format } from "date-fns";
import { connect } from "react-redux";


import { retrieveCustomer } from "../../../store/customer_slice";
import { retrieveInvoice } from "../../../store/invoice_slice";
import { findOrderLineByOrderID } from "../../../store/orderline_slice";
import { retrieveOrder } from "../../../store/order_slice";


class OrderCard extends Component {
	constructor(props) {
	  super(props);

	  this.handleOnClick = this.handleOnClick.bind(this);
	  this.getOrderCardData = this.getOrderCardData(this);

	}

	handleOnClick(tabData) {
		this.props.orderCardOnClick(tabData)
	}

	getOrderCardData(order) {

		this.props.retrieveCustomer({ customerID: order.customerID});
		this.props.retrieveOrder({ orderID: order.orderID});
		this.props.retrieveInvoice({ orderID: order.orderID});
		this.props.findOrderLineByOrderID({ orderID: order.orderID});

	  }



	// This gets the users when the component loads
	componentDidMount() {
		const {order} = this.props;

		this.props.retrieveOrder(this.props.order.orderID);
		this.props.retrieveCustomer(this.props.order.customerID);
		this.props.retrieveInvoice(this.props.order.orderID);
		this.props.findOrderLineByOrderID(this.props.order.orderID);
	}

	render() {
		console.log(this.props);
		const {order, customers, orderlines, invoice} = this.props;

		var tabData = {
			order: this.props.order,
			customer: customers.filter(customer => customer.customerID === order.customerID)
		}
		// customer={}
		var numberOfLogs = 0;
		// order.productsOrdered.forEach(product => numberOfLogs+= product.quantity)
		var firstName = '', lastName = '';
		if (tabData.customer !== undefined) {
			console.log(tabData.customer);
			console.log(tabData.customer.firstName + ', ' + tabData.customer.lastName);
			firstName += tabData.customer.firstName;
			lastName += tabData.customer.lastName;
			console.log(firstName);
		}
		var datePlaced = new Date(tabData.order.datePlaced);



		return (
			<div className='OrderCard' onClick={() => this.handleOnClick(tabData)}>
				<div className='OrderCardHeader' style={{ backgroundColor: this.props.color }}>
					<h4 style={{ fontWeight: 'bold' }}>{tabData.customer.lastName}, {tabData.customer.firstName}</h4>
					{/* <h4 style={{float: 'right'}}>Placed: {format(datePlaced, "MM-dd-yy")}</h4> */}
				</div>
				<div className='OrderCardContent'>
					<h4>Reference: {tabData.order.referenceNumber}</h4>
					<h4>Invoice: {(tabData.order.invoiceNumber === undefined) ? 'NA' : tabData.order.invoiceNumber}</h4>
					<h4>Logs Ordered: {numberOfLogs}</h4>
				</div>	
			</div>
		);	
	}
  }


  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {	
	const {orders, customers, invoices ,orderlines} = state;

	return {
		orders,
		customers,
		invoices,
		orderlines
	};
  };

export default connect(mapStateToProps, {  retrieveOrder, retrieveCustomer, findOrderLineByOrderID, retrieveInvoice })(OrderCard);
