import React, {useState, Component} from "react";
import { format } from "date-fns";
import { connect } from "react-redux";


import { retrieveCustomer ,retrieveCustomers} from "../../../store/customer_slice";
import { retrieveInvoice, retrieveInvoices } from "../../../store/invoice_slice";
import { findOrderLineByOrderID, retrieveOrderLines } from "../../../store/orderline_slice";
import { retrieveOrder, retrieveOrders } from "../../../store/order_slice";


class OrderCard extends Component {
	constructor(props) {
	  super(props);

	  this.handleOnClick = this.handleOnClick.bind(this);
	  this.getCustomer = this.getCustomer.bind(this);


	  this.state = {

		 tabData: {
			order: {},
			customer: {}
		},
	 };
	}

	handleOnClick(tabData) {
		this.props.orderCardOnClick(tabData)
	}

	// This gets the users when the component loads
	componentDidMount() {
		const order = this.props.order;

		this.props
			.retrieveCustomer(id= order.customerID)
			.unwrap()
			.then((reponse) => {
			console.log(reponse);

			this.setState({ message: "The user was updated successfully!" });
			})
			.catch((e) => {
			console.log(e);
			});

		this.props.retrieveOrders();
		this.props.retrieveCustomers();
		this.props.retrieveInvoices();
		this.props.retrieveOrderLines();
	}

	getCustomer(id) {
		this.props.retrieveCustomer(id)
	}

	render() {
		console.log(this.props);
		const {order, orders,customers, orderlines, invoice} = this.props;
		const cardCustomer = customers.filter((customer) => customer.customerID === order.customerId);

		console.log(cardCustomer)
		console.log(tabData);

		// customer={}
		var numberOfLogs = 0;
		// order.productsOrdered.forEach(product => numberOfLogs+= product.quantity)
		var firstName = '', lastName = '';
		if (tabData.customer !== undefined) {
			console.log(tabData.customer);
			// console.log(tabData.customer.firstName + ', ' + tabData.customer.lastName);
			firstName += tabData.customer.firstName;
			lastName += tabData.customer.lastName;
			console.log(firstName);
		}
		var datePlaced = new Date(tabData.order.datePlaced);



		return (
			<div className='OrderCard' onClick={() => this.handleOnClick(tabData)}>
				<div className='OrderCardHeader' style={{ backgroundColor: this.props.color }}>
					<h4 style={{ fontWeight: 'bold' }}>{tabData.customer.lastName}, {tabData.customer.firstName}</h4>
					{/* <h4 style={{float: 'right'}}>Placed: {format(datePlaced, "MM-dd-yy")}</h4> */}
				</div>
				<div className='OrderCardContent'>
					<h4>Reference: {tabData.order.referenceNumber}</h4>
					<h4>Invoice: {(tabData.order.invoiceNumber === undefined) ? 'NA' : tabData.order.invoiceNumber}</h4>
					<h4>Logs Ordered: {numberOfLogs}</h4>
				</div>	
			</div>
		);	
	}
  }


  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {	
	const {orders, customers, invoices ,orderlines} = state;

	return {
		orders: state.orders,
		customers: state.customers,
		invoices: state.invoices,
		orderlines: state.orderlines
	};
  };

export default connect(mapStateToProps, {  retrieveOrder, retrieveCustomers, retrieveOrders, retrieveOrderLines,retrieveInvoices, retrieveCustomer, findOrderLineByOrderID, retrieveInvoice })(OrderCard);
