import React, {Component} from "react";
import { connect } from "react-redux";

import { retrieveProducts } from "../../../store/product_slice";
import { findOrderLineByOrderID } from "../../../store/orderline_slice";

class ProductInfo extends Component {
	constructor(props) {
	  super(props);

	  this.getInvoiceData = this.getInvoiceData.bind(this);

	  this.state = {
        orderID: "",
		invoiceNumber: "NA",
		revenue: "",
		expense: "",
		isPaid: "",
	 };
	}

	// This gets the users when the component loads
	componentDidMount() {
		this.getInvoiceData(this.props.order)
	}

	getInvoiceData(id) {
		this.props
			.retrieveInvoice({id})
			.then((data) => {
				console.log(data);
				this.setState({
                    orderID: data.payload.orderID,
					invoiceNumber: data.payload.invoiceNumber,
					revenue: data.payload.revenue,
                    expense: data.payload.expense,
                    isPaid: data.payload.isPaid,
				});
			})
			.catch((e) => {
			console.log(e);
			});
	}


	render() {
		const {invoices} = this.props;
		console.log(invoices);

		const {invoiceNumber, revenue, expense, isPaid} = this.state;
        console.log(invoiceNumber)

        return (
            <div id='OrderView_Products_Details'>
            <h4 style={{fontWeight: 'bold'}}>Flavor Information</h4>
            <div className='Row'>
                <table>
                    <tr>
                        <th><h4 style={{fontWeight: 'bold'}}>Name</h4></th>
                        <th><h4 style={{fontWeight: 'bold'}}>SKU</h4></th>
                        <th><h4 style={{fontWeight: 'bold'}}>Quantity</h4></th>
                    </tr>
                    {order.productsOrdered.map((product) => (
                        <tr>
                            <td><h4>{product.product.name}</h4></td>
                            <td><h4>{product.product.id}</h4></td>
                            <td><h4>{product.quantity}</h4></td>
                        </tr>					
                    ))}
                </table>
                <table>
                    <tr>
                        <th><h4 style={{fontWeight: 'bold'}}>At a Glance:</h4></th>
                    </tr>
                    <tr>
                        <td><h4 style={{fontWeight: 'bold'}}>Total Number of Logs:</h4></td>
                        <td><h4>{numberOfLogs}</h4></td>
                    </tr>
                    <tr>
                        <td><h4 style={{fontWeight: 'bold'}}>Total Number of Flavors:</h4></td>
                        <td><h4>{numberOfFlavors}</h4></td>
                    </tr>
                </table>
            </div>
            </div> 
        );	
	}
  }


  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {	
	return {
        products: state.products,
        orderlines: state.orderline,
    };
  };

export default connect(mapStateToProps, { retrieveProducts, findOrderLineByOrderID })(ProductInfo);
