import React, { useState } from 'react';
import FlavorOption from './FlavorOption';

function FlavorSelector() {
  const [flavors, setFlavors] = useState([
    { flavor: 'chocolate', quantity: 1 },
    { flavor: 'vanilla', quantity: 2 },
    { flavor: 'strawberry', quantity: 3 },
  ]);
  const [newFlavor, setNewFlavor] = useState('');

  const handleFlavorChange = (event, index) => {
    const newFlavors = [...flavors];
    newFlavors[index].flavor = event.target.value;
    setFlavors(newFlavors);
  };

  const handleQuantityChange = (event, index) => {
    const newFlavors = [...flavors];
    newFlavors[index].quantity = parseInt(event.target.value);
    setFlavors(newFlavors);
  };

  const handleAddFlavor = () => {
    if (newFlavor !== '') {
      setFlavors([...flavors, { flavor: newFlavor, quantity: 1 }]);
      setNewFlavor('');
    }
  };

  return (
    <div>
      {flavors.map((flavor, index) => (
        <FlavorOption
          key={index}
          flavorOption={flavor.flavor}
          quantityOption={flavor.quantity}
          onFlavorChange={(event) => handleFlavorChange(event, index)}
          onQuantityChange={(event) => handleQuantityChange(event, index)}
        />
      ))}

      <div className="new-flavor">
        <input
          type="text"
          value={newFlavor}
          placeholder="New flavor"
          onChange={(event) => setNewFlavor(event.target.value)}
        />
        <button onClick={handleAddFlavor}>Add Flavor</button>
      </div>
    </div>
  );
}

export default FlavorSelector;
