import React, {Component} from "react";
import { format } from "date-fns";
import { connect } from "react-redux";


import { retrieveCustomer ,retrieveCustomers} from "../../../store/customer_slice";
import { retrieveInvoice, retrieveInvoices } from "../../../store/invoice_slice";
import { findOrderLineByOrderID, retrieveOrderLines } from "../../../store/orderline_slice";
import { retrieveOrder, retrieveOrders } from "../../../store/order_slice";


class OrderCard extends Component {
	constructor(props) {
	  super(props);

	  this.handleOnClick = this.handleOnClick.bind(this);
	  this.getCustomer = this.getCustomer.bind(this);


	  this.state = {

		 tabData: {
			order: {},
			customer: {}
		},

	 };
	}

	handleOnClick(tabData) {
		this.props.orderCardOnClick(tabData)
	}

	// This gets the users when the component loads
	componentDidMount() {
		// this.props.retrieveOrders();
		this.props.retrieveCustomers();
		this.props.retrieveInvoices();
		this.props.retrieveOrderLines();

		this.setState({
			tabData: {

				order: this.props.order,
				customer: this.props.customers.filter(customer=> customer.customerID === this.props.order.customerId)
			}
		})

	}

	getCustomer(id) {

		console.log(this.props.order)

		return this.props
			.retrieveCustomer(id)
			.then((data) => {
				console.log(data)
				this.setState({
					order: this.props.order,
					customer: data,
				  });
				console.log(data);
			  })
			.catch((e) => {
			console.log(e);
			});

	}

	render() {
		const {order, customers, orderlines, invoices} = this.props;
		// const {customerOnCard} = customers.filter(customer=> customer.customerID === order.customerId)
		console.log(this.props);


		// customer={}
		var numberOfLogs = 0;
		// order.productsOrdered.forEach(product => numberOfLogs+= product.quantity)		
		// var datePlaced = new Date(order.datePlaced);



		return (
			<div className='OrderCard' onClick={() => this.handleOnClick(this.state.tabData)}>
                    {customers &&
                      customers.filter((customer)=> customer.customerID === order.customerId).map((customer, index) => (
						<div className='OrderCardHeader' style={{ backgroundColor: this.props.color }}>
							<h4 style={{ fontWeight: 'bold' }}>{customer.lastName}, {customer.firstName}</h4>
							{/* <h4 style={{float: 'right'}}>Placed: {format(datePlaced, "MM-dd-yy")}</h4> */}
						</div>
                        // <li className={"list-group-item " + (index === currentIndex ? "active" : "")}
                        //     onClick={() => this.setActiveUser(user, index)} key={index}>
                        //     {user.nickname}
                        //     <button onClick={this.updateContent} type="submit">Edit</button>
                        //     <button onClick={this.removeUser} type="delete">Delete</button>
                        // </li>
                      ))}

				<div className='OrderCardContent'>
					<h4>Reference: {order.referenceNumber}</h4>
					{/* <h4>Invoice: {(order.invoiceNumber === undefined) ? 'NA' : order.invoiceNumber}</h4>
					<h4>Logs Ordered: {numberOfLogs}</h4> */}
				</div>	
			</div>
		);	
	}
  }


  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {	
	const {orders, customers, invoices ,orderlines} = state;

	return {
		customer: state.customers,
		invoices: state.invoices,
		orderlines: state.orderlines
	};
  };

export default connect(mapStateToProps, {  retrieveOrder, retrieveCustomers, retrieveOrders, retrieveOrderLines,retrieveInvoices, retrieveCustomer, findOrderLineByOrderID, retrieveInvoice })(OrderCard);
