import React, { useState } from 'react';

function FlavorSelector() {
  const [flavor, setFlavor] = useState('chocolate');
  const [quantity, setQuantity] = useState(1);
  //get these from props.products
  const [flavorOptions, setFlavorOptions] = useState([
    'plain',
    'fiery fig',
    'herb garlic',
  ]);

  const handleFlavorChange = (event) => {
    setFlavor(event.target.value);
  };

  const handleQuantityChange = (event) => {
    setQuantity(event.target.value);
  };

  const handleAddFlavor = () => {
    setFlavorOptions([...flavorOptions, 'new flavor']);
  };

  return (
    <div>
      <label htmlFor="quantity-input">Quantity:</label>
      <input
        type="number"
        id="quantity-input"
        value={quantity}
        onChange={handleQuantityChange}
      />

      <label htmlFor="flavor-dropdown">Flavor:</label>
      <select id="flavor-dropdown" value={flavor} onChange={handleFlavorChange}>
        {flavorOptions.map((flavorOption) => (
          <option key={flavorOption} value={flavorOption}>
            {flavorOption}
          </option>
        ))}
      </select>

      <button onClick={handleAddFlavor}>Add Flavor</button>
    </div>
  );
}

export default FlavorSelector;
