import add_icon from '../../assets/img/plus_black.svg'
import edit_icon from '../../assets/img/edit_white.svg'
import delete_icon from '../../assets/img/trash_black.svg'
import "../../assets/style/tab_newOrder.css"

import React, { useState, Component } from 'react'
import { connect, useDispatch, useSelector } from "react-redux";
import moment from 'moment';

import { createOrder } from '../../store/order_slice'
import {createShippingAddress} from '../../store/address_slice'
import { createCustomer } from '../../store/customer_slice';
import { createOrderLine } from "../../store/orderline_slice";
import { retrieveProducts } from "../../store/product_slice";
import Dropdown  from './orderview_components/Dropdown';
import {stateOptions} from '../../assets/util/dropdown.constants'
import { CustomerInfoEdit, GeneralInfoEdit, ProductInfoEdit, ShippingInfoEdit } from './EditComponents'


// ------------------------------ VIEW --------------------------------- //


const NewInvoiceInfoView = () => {

		return (
			<div id='NewInvoiceView' className='GenericBackgroundAdd'>
				<label className='BoxDescriptionTitle'>Invoice</label>
			   	<div className='MissingBackground'>Invoice Generated Upon Save</div>
		   </div> 
	   );

}





// ------------------------------ CLASS STARTS HERE --------------------------------- //

class NewOrder extends Component {
	constructor(props) {
	  super(props);
		this.handleInputChange = this.handleInputChange.bind(this)

		this.handleInputActiveOrder = this.handleInputActiveOrder.bind(this)
		this.handleInputActiveCustomer = this.handleInputActiveCustomer.bind(this)

		this.refreshState = this.refreshState.bind(this)

		this.handleSelfOrderChange = this.handleSelfOrderChange.bind(this)
		this.handleIsGiftChange = this.handleIsGiftChange.bind(this)


		this.deleteCurrOrder = this.deleteCurrOrder.bind(this)
		this.saveEdits = this.saveEdits.bind(this)

	  this.state = {
		orderID: -1,
		activeOrder: {
			referenceNumber: "",
			datePlaced: "",
			orderStatus: "Placed",
			trackingNumber: "",
			giftFor: "",
			giftMessage: "",
			isGift: 0,
			customerId: 0,
			shippingId: 0,
			isSelfOrder:0,

		},
		activeCustomer: {
			firstName: "",
			lastName: "",
			email: "",
			phoneNumber: "",
		},
		activeAddress: {
			streetAddressOne: "",
			streetAddressTwo: "",
			city: "",
			state: "",
			zip: ""
		},
		activeInvoice: {
			invoiceNumber: "NA",
			customerPaid:0,
			revenue: 0,
			expense: 0,
			invoiceStatus: "Missing",
		},
		productsOrdered: [

		],
		numberOfLogs: 0,
		numberOfFlavors: 0,
		viewOnly: true,
		isSelfOrderToggle:false,
		isGiftToggle:false
	  }
	}

	// NOT WORKING
	refreshState() {
		this.setState({
				orderID: -1,
				activeOrder: {
					referenceNumber: "",
					datePlaced: "",
					orderStatus: "Placed",
					trackingNumber: "",
					giftFor: "",
					giftMessage: "",
					isGift: 0,
					customerId: 0,
					shippingId: 0,
					isSelfOrder:0,

				},
				activeCustomer: {
					firstName: "",
					lastName: "",
					email: "",
					phoneNumber: "",
				},
				activeAddress: {
					streetAddressOne: "",
					streetAddressTwo: "",
					city: "",
					state: "",
					zip: ""
				},
				activeInvoice: {
					invoiceNumber: "NA",
					customerPaid:0,
					revenue: 0,
					expense: 0,
					invoiceStatus: "",
				},
				productsOrdered: [

				],
				numberOfLogs: 0,
				numberOfFlavors: 0,
				viewOnly: false,
				isSelfOrderToggle:false,
				isGiftToggle:false

		})

	}

    handleInputChange(e) {
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
        [name]: value
        });

		console.log(this.state)
    }

	handleSelfOrderChange(e) {
		console.log("clicking")
		this.setState({
			isSelfOrderToggle: !this.state.isSelfOrderToggle
		})
	}

	handleIsGiftChange(e) {
		this.setState({
			isGiftToggle: !this.state.isGiftToggle
		})
	}

	handleInputActiveOrder(e) {
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
			activeOrder: {
				...this.state.activeOrder,
				[name]: value
			}
        });

		console.log(this.state)
    }

	handleInputActiveCustomer(e) {
        const target = e.target;
