import React, {Component} from "react";
import { format } from "date-fns";
import { connect } from "react-redux";


import { retrieveCustomer ,retrieveCustomers} from "../../../store/customer_slice";
import { retrieveInvoice, retrieveInvoices } from "../../../store/invoice_slice";
import { findOrderLineByOrderID, retrieveOrderLines } from "../../../store/orderline_slice";
import { retrieveOrder, retrieveOrders, findCustomerThroughOrder } from "../../../store/order_slice";


class OrderCard extends Component {
	constructor(props) {
	  super(props);

	  this.handleOnClick = this.handleOnClick.bind(this);
	  this.getCustomer = this.getCustomer.bind(this);


	  this.state = {

		 tabData: {
			order: {},
			customer: {}
		},

	 };
	}

	handleOnClick(tabData) {
		this.props.orderCardOnClick(tabData)
	}

	// This gets the users when the component loads
	componentDidMount() {
		// this.props.retrieveOrders();
		this.props.retrieveCustomers();
		this.props.retrieveInvoices();
		this.props.retrieveOrderLines();
	}

	// getCustomer(order) {


	// 	const customerId = order.customerId;
	// 	this.props
	// 		.retrieveCustomer(1)
	// 		.then((data) => {
	// 			console.log(data)
	// 			this.setState({
	// 				tabData: {
	// 					order: order,
	// 					customer: data,
	// 				}
	// 			  });
	// 			console.log(data);
	// 		  })
	// 		.catch((e) => {
	// 		console.log(e);
	// 		});

	// }
	getCustomer(id) {
		console.log("this is the id: "+ id)


		this.props
			.retrieveCustomer({id})
			.then((data) => {
				console.log(data);
				this.setState({
					tabData: {
						customer: data,
					}
				  });
			  })
			.catch((e) => {
			console.log(e);
			});

	}

	render() {
		const {order, customers, orderlines, invoices} = this.props;
		console.log(this.props);

		this.getCustomer(order.customerId)
		console.log(this.props);
		const cardCustomer = customers.filter(customer => (customer.customerId === order.customerId))
		console.log(orderlines)
		console.log(cardCustomer)


		// customer={}
		var numberOfLogs = 0;
		// order.productsOrdered.forEach(product => numberOfLogs+= product.quantity)		
		// var datePlaced = new Date(order.datePlaced);



		return (
			<div className='OrderCard' onClick={() => this.handleOnClick(this.state.tabData)}>

				<div className='OrderCardHeader' style={{ backgroundColor: this.props.color }}>
					<h4 style={{ fontWeight: 'bold' }}>{this.state.tabData.customer.lastName}, {this.state.tabData.customer.firstName}</h4>
					{/* <h4 style={{float: 'right'}}>Placed: {format(datePlaced, "MM-dd-yy")}</h4> */}
				</div>

				<div className='OrderCardContent'>
					<h4>Reference: {order.referenceNumber}</h4>
					<h4>ID: {order.orderID}</h4>
					<h4>CustomerID: {order.customerId}</h4>


					{/* <h4>Invoice: {(order.invoiceNumber === undefined) ? 'NA' : order.invoiceNumber}</h4>
					<h4>Logs Ordered: {numberOfLogs}</h4> */}
				</div>	
			</div>
		);	
	}
  }


  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {	

	return {
		customers: state.customers,
		invoices: state.invoices,
		orderlines: state.orderline
	};
  };

export default connect(mapStateToProps, {  findCustomerThroughOrder,retrieveOrder, retrieveCustomers, retrieveOrders, retrieveOrderLines,retrieveInvoices, retrieveCustomer, findOrderLineByOrderID, retrieveInvoice })(OrderCard);
