import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link} from "react-router-dom";

import NavBar from './views/nav/Navbar';
import './App.css';
import Dashboard from './views/dashboard/Dashboard';
import OrderList from './views/orders/OrdersList';
import Search from './views/search/Search';
import LoginPage from './views/aa_login/LoginPage';
import Invoices from './views/invoices/Invoices';
import AdminPanel from './views/admin/AdminPanel';

// For Navigation
export const ViewIndex = {
	DASHBOARD: 1,
	ORDERS: 2,
	INVOICES: 3,
	SEARCH: 4,
	HELP: 5,
	LOGOUT: 6,
  TEST: 7
}

// Will this be controlled by the state?
const UserStatus = {
	LOGGED_IN: 1,
	LOGGED_OUT: 2,
}


function App() {

	const [displayContent, setDisplayContent] = useState(ViewIndex.DASHBOARD) // BUT ALSO LINK TO 

  // get status of user
  /**
   * USER STATUS??
   */
	//const [userStatus, setUserStatus] = useState(UserStatus.GGC)

  // depending on user status, will depend which class to run (App will be login?)

  //	{/* if userStatus is ggc show this */}

  // keep viewIndex so NavBar is set with a flag that specifies the specific views allowed to be show
  return (
      <div className="App">
          <header className='App-header'>            
              <NavBar activeButton={displayContent} buttonOnClick={setDisplayContent}/>
                <Route path="/" element={Dashboard}> {displayContent === ViewIndex.DASHBOARD} </Route>
                <Route path="/Orders" element={OrderList}> {displayContent === ViewIndex.ORDERS} </Route>
                <Route path="/Invoices" element={Invoices}> {displayContent === ViewIndex.INVOICES} </Route>
                <Route path="/Search" element={Search}> {displayContent === ViewIndex.SEARCH} </Route>
                <Route path="/AdminSettings" element={AdminPanel}> {displayContent === ViewIndex.TEST} </Route>
                <Route path="/Help" element={Dashboard}> {displayContent === ViewIndex.DASHBOARD} </Route>
                <Route path="/Login" element={Dashboard}> {displayContent === ViewIndex.DASHBOARD} </Route>


            {/* We used to just stack the nav components here but I moved to Route elements. EX. below: */}
            {/* {displayContent === ViewIndex.LOGOUT && <LoginPage />} */}
          </header>
        </div>    
  );


}

export default App;
