import React, { Component } from "react";
import { connect } from "react-redux";
import PermissionTypes from "../../App"

import { addUser, retrieveUsers, deleteAllUsers, retrieveUser , updateUser, deleteUser, findUsersByPermission } from "../../store/user_slice";

class AdminPanel extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    // this.onChangeEmail = this.onChangeEmail.bind(this);
    // this.onChangePassword = this.onChangePassword.bind(this);
    // this.onChangeNickname = this.onChangeNickname.bind(this);
    // this.onChangePermission = this.onChangePermission.bind(this);

    // this.onChangePermissionFilter = this.onChangePermissionFilter.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveUser= this.setActiveUser.bind(this);
    // this.findUsersByPermission = this.findUsersByPermission.bind(this);
    this.removeAllUsers = this.removeAllUsers.bind(this);

    this.saveUser = this.saveUser.bind(this);
    this.refreshUser = this.refreshUser.bind(this);

    this.state = {
      id: null,

      email: "",
      password: "",
      nickname: "",
      permissionType: "", 

      currentUser: null,
      currentIndex: -1,

    };
  }

  // This gets the users when the component loads
  componentDidMount() {
    this.props.retrieveUsers();
  }

  handleInputChange(e) {
    const target = e.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }
  // onChangeEmail(e) {
  //   this.setState({
  //     email: e.target.value,
  //   });
  //   console.log("editing email");
  // }

  // onChangePassword(e) {
  //   this.setState({
  //     password: e.target.value,
  //   });
  //   console.log("editing pwro");
  // }

  // onChangeNickname(e) {
  //   this.setState({
  //     nickname: e.target.value,
  //   });
  //   console.log("editing nick");
  // }

  // onChangePermission(e) {
  //   this.setState({
  //     permissionType: e.target.value,
  //   });
  //   console.log("editing perm");
  // }

  saveUser() {
    const { currentUser, currentIndex } = this.state;
    const {email, password, nickname, permissionType} = this.state;

    this.props
      .addUser({email, password, nickname, permissionType})
      .then((data) => {
        this.setState({
          id: data.id,

          email: data.email,
          password: data.password,
          nickname: data.nickname,
          permissionType: data.permissionType, 
        });
        console.log(data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  refreshUser() {
    this.setState({
      id: null,

      email: "",
      password: "",
      nickname: "",
      permissionType: "", 

    });
  }

  refreshList() {
    this.setState({
      currentUser: null,
      currentIndex: -1,
    });
  }

  setActiveUser(user, index) {
    this.setState({
      currentUser: user,
      currentIndex: index,
    });
  }

  removeAllUsers() {
    this.props
      .deleteAllUsers()
      .then((data) => {
        console.log(data);
        this.refreshList();
        this.refreshList();
      })
      .catch((e) => {
        console.log(e);
      });
  }

  //removeUser(user)

  render() {
    const {users} = this.props;
    console.log(this.props);
    const { currentUser, currentIndex } = this.state;

    return (
        <div id='OrderDetails'>
            <h1>TEST:admin?</h1>
              <div>
                <div id='OrderView_Header'>
                    {/**on submit i want to dispatch the create user action  */}
                    <button onClick={this.saveUser} type="submit">Save</button>
                    <button onClick={this.refreshUser}>Cancel</button>
                    <button className="m-3 btn btn-sm btn-danger" onClick={this.removeAllUsers} > Remove All </button>
                </div>
                <div className="list_users">
                  <h3>List of users</h3>
                  <ul className="list-group">
                    {users &&
                      users.map((user, index) => (
                        <li className={"list-group-item " + (index === currentIndex ? "active" : "")}
                            onClick={() => this.setActiveUser(user, index)} key={index}>
                            {user.nickname}
                        </li>
                      ))}
                  </ul>

                </div>

                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="text"
                    className="form-control"
                    id="email"
                    required
                    value={this.state.email}
                    onChange={this.onChangeEmail}
                    name="email"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input
                    type="text"
                    className="form-control"
                    id="password"
                    value={this.state.password}
                    onChange={this.onChangePassword}
                    name="password"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="nickname">Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="nickname"
                    value={this.state.nickname}
                    onChange={this.onChangeNickname}
                    name="nickname"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="message">Permission Type</label>
                  <input
                    type="text"
                    className="form-control"
                    id="message"
                    value={this.state.permissionType}
                    onChange={this.onChangePermission}
                    name="message"
                  />
                </div>
              </div>
        </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
  };
};

export default connect(mapStateToProps, { addUser, retrieveUsers, retrieveUser, updateUser, deleteUser, deleteAllUsers, findUsersByPermission })(AdminPanel);
