import React from "react";
import { format } from "date-fns";

import { retrieveCustomer } from "../../../store/customer_slice";

// Cards are part of the list
export const OrderCard = ({ order, color, orderCardOnClick }) => {
	// customer={customers.filter(customer => customer.customerID === order.customerId)[0]}
	var numberOfLogs = 0;
	// order.productsOrdered.forEach(product => numberOfLogs+= product.quantity)
	var firstName = '', lastName = '';
	if (customer !== undefined) {
		console.log(customer);
		console.log(customer.firstName + ', ' + customer.lastName);
		firstName += customer.firstName;
		lastName += customer.lastName;
		console.log(firstName);
	}
	var datePlaced = new Date(order.datePlaced);


	// var firstName = customer.firstName;
	// var lastName = customer.lastName;
	// console.log(order)
	return (
		<div className='OrderCard' onClick={orderCardOnClick(order)}>
			<div className='OrderCardHeader' style={{ backgroundColor: color }}>
				<h4 style={{ fontWeight: 'bold' }}>{lastName}, {firstName}</h4>
				<h4 style={{float: 'right'}}>Placed: {format(datePlaced, "MM-dd-yy")}</h4>
			</div>
			<div className='OrderCardContent'>
				<h4>Reference: {order.referenceNumber}</h4>
				<h4>Invoice: {(order.invoiceNumber === undefined) ? 'NA' : order.invoiceNumber}</h4>
				<h4>Logs Ordered: {numberOfLogs}</h4>
			</div>
		</div>
	);
};


class ActiveOrdersTab extends Component {
	constructor(props) {
	  super(props);
	}

	// This gets the users when the component loads
	componentDidMount() {
	  this.props.retrieveOrders();
	  this.props.retrieveCustomers();
	  this.props.retrieveInvoices();
	  this.props.retrieveOrderLines();
	}

	render() {
		const {orders, customers, orderline, invoices} = this.props;

		const currentActiveOrders = orders.filter(order => (order.orderStatus === "Placed" || order.orderStatus === 'Processed' || order.orderStatus === 'Shipped'));

		console.log(this.props)
		return(
			<div id='ActiveOrders'>	
				<div id='OrdersDisplay' style={{display: 'flex', displayDirection: 'column'}}>
					<ActiveOrders orders={currentActiveOrders} orderCardOnClick={this.props.orderCardOnClick}/>
				</div>
			</div>
		);

	}
  }


  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {
	return {
	  orders: state.orders,
	  customers: state.customers,
	  orderline: state.orderline,
	  invoices: state.invoices,
	};
  };

export default connect(mapStateToProps, { createOrder, retrieveOrders, retrieveCustomers, retrieveOrderLines, retrieveInvoices })(OrderCard);
