  // Mapping only the parts of the redux store that we want to work with on this component
  const mapStateToProps = (state) => {
	return {
        shippingAddresses: state.shippingAddresses,
		customers: state.customers,
		invoices: state.invoices,
		orderline: state.orderline,
		products: state.products
	};
  };

export default connect(mapStateToProps, { 
	deleteOrder, 
	updateOrder, updateShippingAddress, updateCustomer, updateInvoice, updateOrderLine, 
	retrieveShippingAddress, retrieveInvoice, retrieveOrder, retrieveCustomer
 })(ExistingOrder);
