	const [flavorSelectors, setFlavorSelectors] = useState(
		[<FlavorSelector key={0} onChange={(quantity, flavor) => 
			handleFlavorSelectorChange(0, quantity, flavor)} />]);

	const handleAddFlavorSelector = () => {
	  const newKey = flavorSelectors.length;
	  const newFlavorSelectors = [...flavorSelectors];
	  newFlavorSelectors.push(<FlavorSelector key={newKey} onChange={(quantity, flavor) => handleFlavorSelectorChange(newKey, quantity, flavor)} />);
	  setFlavorSelectors(newFlavorSelectors);
	};

	const handleFlavorSelectorChange = (index, quantity, flavor) => {
	  // Update state with the selected quantity and flavor
	  console.log(`FlavorSelector ${index} changed: ${quantity} ${flavor}`);
	};
	return (
		<div id='NewOrderProducts' className='GenericBackgroundAdd'>
			<label className='BoxDescriptionTitle'>Order Details</label>
			<FlavorSelector/>
			<button onClick={handleAddFlavorSelector}>Add Flavor</button>

		</div> 
	);	

}
